mapper.select:

select * from (
select /*+ index_desc(one_user one_username) */ rownum rn, u.username, c.corname, c.cornum, c.tel, c.addr, c.profile, c.name, u.active, u.regdate, p.pno, p.picture, p.regdate pr  
from one_product p, one_cor c, one_user u 
where 



p.username=c.username and c.username=u.username and rownum <= #{pageNum} * #{amount} order by u.regdate desc
)where rn > (#{pageNum}-1) * #{amount}


select * from (
select /*+ index_desc(one_user one_username) */ rownum rn, u.username, c.corname, c.cornum, c.tel, c.addr, c.profile, c.name, u.active, u.regdate, p.pno, p.picture, p.regdate pr  
from one_product p, one_cor c, one_user u 
where 



p.username=c.username and c.username=u.username and rownum <= #{pageNum} * #{amount} order by c.corname
)where rn > (#{pageNum}-1) * #{amount}
